import { RadixAtom, RadixBase64, RadixEncryptor, RadixKeyPair } from '../RadixAtomModel';
export default abstract class RadixPayloadAtom extends RadixAtom {
    encryptor: RadixEncryptor;
    encrypted: RadixBase64;
    constructor(json?: object);
    getDecryptedPayload(keyPair: RadixKeyPair): any;
    addEncryptedPayload(payload: string, recipients: RadixKeyPair[]): void;
    addUnencryptedPayload(payload: string): void;
}
